import React, { useState, useEffect } from 'react'

import { Container, DropFiles, SubmitButton, DropFilesZone } from './styles'
import Dropzone from 'react-dropzone'
import apiCodeContest from '../../services/apiCodeContest'

import { Modal, Spinner, Col, Row } from 'react-bootstrap'
import { useFetch } from 'src/hooks/useFetch'

export interface Props {
  challengeName?: string;
}
interface BodyRequest {
  [key: string]: any
}
interface Push {
  data: {
    commit: {
      sha: string
    }
  }
  id: number
}

interface Run {
  data: {
    status: string,
    conclusion: string,
    // eslint-disable-next-line camelcase
    head_commit: {
      id: string
    },
    id: number
  }
}

interface Runs {
  // eslint-disable-next-line camelcase
  workflow_runs: [
    Run['data']
  ],
  // eslint-disable-next-line camelcase
  total_count: number
}

const Drop: React.FC<Props> = ({ challengeName }) => {
  const [fileName, setFileName] = useState('')
  const [commitId, setCommitId] = useState<string>()
  const [solution, setSolution] = useState<string>('')
  const [inProgress, setInProgress] = useState(false)
  const [sendedFile, setSendedFile] = useState(false)
  const [show, setShow] = useState(false)
  const [currentChallengeName, setCurrentChallengeName] = useState<string>('challenge')
  const [bodyRequest, setBodyRequest] = useState <BodyRequest | null >(null)

  const { data: runs } = useFetch<Runs>('/actions/runs')

  const config = {
    headers: {
      Authorization: `token ${process.env.REACT_APP_TOKEN}`,
      Accept: 'application/vnd.github.antiope-preview+json'
    }
  }

  if (challengeName && challengeName !== currentChallengeName) {
    setCurrentChallengeName(challengeName)
    setFileName('')
    setBodyRequest(null)
    setSendedFile(false)
    setShow(false)
    setSolution('')
    setInProgress(false)
  }

  const handleDrop = (acceptedFile: any) => {
    setSendedFile(false)
    setFileName(acceptedFile.map((file: any) => {
      const reader = new FileReader()
      reader.readAsDataURL(file)
      reader.addEventListener('load', async () => {
        if (reader) {
          const result = (reader.result as string).split(',')[1]

          const bodyRequest: BodyRequest = {
            message: `${challengeName?.split(' ').join('_')}/user1`,
            committer: {
              name: 'minecodebot',
              email: 'minecode.geral@gmail.com'
            },
            content: `${result}`
          }

          try {
            const fileAlreadyExist = await apiCodeContest.get(`/contents/challenges/${challengeName?.split(' ').join('_')}/user1/resolution.py`, config)
            bodyRequest.sha = `${fileAlreadyExist.data.sha}`
          } catch (error) {
            console.log(error)
          }
          setBodyRequest(bodyRequest)
        }
      }, false)
      return file.name
    }))
  }

  const submitFile = async (bodyRequest: BodyRequest) => {
    setSolution('')
    setInProgress(true)
    setShow(true)
    const push: Push = await apiCodeContest.put(`/contents/challenges/${challengeName?.split(' ').join('_')}/user1/resolution.py`, bodyRequest, config)
    const commit = push.data.commit.sha
    setCommitId(commit)
  }

  useEffect(() => {
    // eslint-disable-next-line camelcase
    if (runs?.workflow_runs[0].head_commit.id === commitId) {
    // eslint-disable-next-line camelcase
      setSolution(runs?.workflow_runs[0].conclusion as string)
      setInProgress(false)
      setSendedFile(true)
    }
    // eslint-disable-next-line camelcase
  }, [runs?.workflow_runs[0].head_commit.id])

  return (
    <Container>
      {challengeName
        ? <Dropzone
          onDrop={handleDrop}
        >
          {({
            getRootProps,
            getInputProps,
            isDragActive,
            isDragReject
          }) => {
            return (
              <DropFiles>
                <DropFilesZone {...getRootProps()}>
                  <input {...getInputProps()} />
                  <span>{isDragActive ? 'üìÇ' : 'üìÅ'}</span>
                  <p>
                    {fileName ? <>{fileName}</> : isDragReject ? <> Invalid file </> : <>Drag & drop images, or click to select files</>}
                  </p>
                </DropFilesZone>

                {inProgress ? <Modal
                  show={show}
                  backdrop="static"
                  keyboard={false}
                >
                  <Modal.Header closeButton>
                    <Modal.Title>Modal title</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                    <Container>
                      <Row>
                        <Col xs={12} className="text-center">
                          <Spinner animation="border" />
                        </Col>
                        <Col xs={12} className="text-center">
                          <p>We are testing your solution... Soon as possibel, your pontuation will be upadated!!</p>
                        </Col>
                      </Row>
                    </Container>
                  </Modal.Body>
                </Modal>
                  : bodyRequest && !sendedFile ? <SubmitButton onClick={() => { submitFile(bodyRequest); setShow(false) }}>Submit</SubmitButton> : sendedFile && solution ? <p>{solution}</p> : <></>}
              </DropFiles>
            )
          }}
        </Dropzone> : <></>
      }
    </Container>
  )
}

export default Drop
