import React, { useCallback } from 'react'

import { ContainerList, Category } from './styles'

import ChallengeButton from '../ChallengeButton'

import { useFetch } from 'src/hooks/useFetch'
import { titleCase } from '../Utils'
import { useSelector, useDispatch } from 'react-redux'

interface Contest {
  tree: Challenge []
}

interface Challenge {
  path: string;
}

const ContestList: React.FC = () => {
  const dispatch = useDispatch()
  const dataAuth = useSelector((state: any) => state.data.auth)

  const { data } = useFetch<Contest>('/git/trees/ba3a18d7f49f2296bfd47ee3db7682f8ed7cb8f1?recursive="true"')
  data?.tree.forEach(element => {
    console.log('Name:', element.path)
  })
  const handleSelectChange = useCallback(
    (id: number) => {
      const challenges = document.getElementById('listOfContests')?.childNodes
        challenges?.forEach((challenge) => {
          if (
            challenge.isEqualNode(
              document.getElementById(id.toString())
            )
          ) {
                data?.tree.forEach((selectedChallenge) => {
                  console.log('selectedchallenge', selectedChallenge.path)
                  // selectedChallenge.forEach(element => {
                  //   const selectedChallengeName = element.name
                  //     .split('_')
                  //     .join(' ')
                  //   if (
                  //     titleCase(selectedChallengeName) ===
                  //       challenge.firstChild?.textContent
                  //   ) {
                  //     const newChallenge = {
                  //       data: {
                  //         auth: dataAuth,
                  //         challenge: { name: selectedChallengeName }
                  //       }
                  //     }
                  //     dispatch({ type: 'CHALLENGE', data: newChallenge })
                  //   }
                  // })
                })
          }
        })
    },
    [data, dataAuth, dispatch]
  )

  return (
    <ContainerList>
      <Category>
        <span>Contests</span>
      </Category>
      <div id='listOfContests'>
        {data?.tree.filter((contest: any) => {
          return contest.name !== 'requirements.txt'
        })
        .map((contest, i) => (
          <div
            key={i}
            id={i.toString()}
            onClick={() => handleSelectChange(i)}
          >
            {contest.path.split('/').length === 1 ? <p>{contest.path}</p>
              : <ChallengeButton
                contestName={contest.path}
              />
            }
          </div>
        ))}
      </div>
    </ContainerList>
  )
}

export default ContestList
