import styled from 'styled-components'

export const Grid = styled.div`
	display: grid;

	grid-template-columns: auto 240px;
	grid-template-rows: 46px auto;

	grid-template-areas:
		'LG LG'
		'C C';

	@media (min-width: 1024px) {
		grid-template-areas:
			'LG LG'
			'C UL';
	}

	max-height: 100vh;
`
