import { createStore } from 'redux'

const INITIAL_STATE = {
  data: {
    authenticated: false,
    user: {
      id: null,
      name: null,
      surname: null,
      image: null
    },
    token: null

  }

}

function authenticated (state = false, action: any) {
  switch (action.type) {
    case 'LOGIN':
      console.log(action)
      return true
    case 'LOGOUT':
      console.log(action)
      return false
    default:
      console.log(action)
      return state
  }
}

const store = createStore(authenticated)

export default store
