import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useFetch } from 'src/hooks/useFetch'

import Authentication from '../Authentication'
import { Container, Avatar, UserInfo, Auth } from './styles'

interface User {
	userId?: string;
	user: UserApi;
	score: number;
}

interface UserApi {
	userId: string;
	firstName: string;
	lastName: string;
	imageUrl: string;
}

const Navbar: React.FC = () => {
  const data = useSelector((state: any) => state.data)
  const dataAuth = data.auth
  const authentication = dataAuth.authenticated
  const dataChallenge = data.challenge
  const challengeName = dataChallenge.name
  // const dataGlobalScore = data.globalScore
  // const dataListOfUsers = data.listOfUsers
  // const dataChallengeScore = data.challengeScore

  const { data: globalScore } = useFetch<User[]>('http://localhost:4001/contests/global')
  const { data: listOfUsers } = useFetch<UserApi[]>('http://localhost:4001/contests/user')
  const { data: challengeScore } = useFetch<User[]>(`http://localhost:4001/contests/global/${challengeName}`)
  if (globalScore || listOfUsers || challengeScore) {
    const newData = {
      data: {
        auth: dataAuth,
        challenge: dataChallenge,
        globalScore: globalScore,
        listOfUsers: listOfUsers,
        challengeScore: challengeScore
      }
    }

    useDispatch({ type: 'CHALLENGE', data: newData })
  }
  return (
    <Container>
      {authentication.authenticated ? (
        <UserInfo>
          <span>Welcome, {authentication.user.name}!</span>
          <Avatar src={authentication.user.image} />
        </UserInfo>
      ) : (
        <></>
      )}
      <Auth>
        <Authentication />
      </Auth>
    </Container>
  )
}

export default Navbar
