import React from 'react'

import { GoogleLogin, GoogleLogout } from 'react-google-login'
import { useDispatch, useSelector } from 'react-redux'

import { Auth } from './styles'

const Authentication: React.FC = () => {
  const data = useSelector((state: any) => state)
  const authentication = data.data_auth.authenticated
  const dataChallenge = data.data_challenge

  const dispatch = useDispatch()

  const login = (response: any) => {
    const newAuth = {
      auth: {
        authenticated: true,
        user: {
          id: response.googleId,
          name: response.profileObj.givenName,
          surname: response.profileObj.familyName,
          image: response.profileObj.ImageUrl
        },
        token: response.tokenId
      }
    }
    dispatch({ type: 'LOGIN', data: { auth: newAuth, challenge: dataChallenge } })
  }

  const badResponseGoogle = (response: any) => {
    console.log(response)
    dispatch({ type: 'BAD_RESPONSE', data: null })
  }

  const logout = () => {
    dispatch({ type: 'LOGOUT', data: null })
  }

  return (
    <Auth>
      {authentication ? (
        <GoogleLogout
          clientId='156221636932-bvl7ocr3bhrkikgcqc99k4g1a1s0sla1.apps.googleusercontent.com'
          buttonText='Logout'
          onLogoutSuccess={logout}
        ></GoogleLogout>
      ) : (
        <GoogleLogin
          clientId='156221636932-bvl7ocr3bhrkikgcqc99k4g1a1s0sla1.apps.googleusercontent.com'
          buttonText='Login'
          onSuccess={login}
          onFailure={badResponseGoogle}
          cookiePolicy={'single_host_origin'}
          isSignedIn={true}
        />
      )}
    </Auth>
  )
}

export default Authentication
