import React from 'react'

import { Container, Grid } from './styles'

import ChallengeInfo from '../ChallengeInfo'

import Drop from '../Drop'
import { useSelector } from 'react-redux'

const Challenge: React.FC = () => {
  const authentication = useSelector((state: any) => state.data_auth.authenticated)
  const challengeName = useSelector((state: any) => state.data_challenge.name)

  return (
    <Container>
      <Grid>
        {challengeName ? (
          <>
            <ChallengeInfo challengeName={challengeName} />
            {authentication ? (
              <Drop challengeName={challengeName} />
            ) : (
              <></>
            )}
          </>
        ) : (
          <></>
        )}
      </Grid>
    </Container>
  )
}

export default Challenge
