import { createStore } from 'redux'

const INITIAL_STATE = {
  data: {
    data_auth: {
      authenticated: false,
      user: {
        id: null,
        name: null,
        surname: null,
        image: null
      },
      token: null
    },
    data_challenge: {
      name: null
    }
  }
}

function authenticated (state: any, action: any) {
  switch (action.type) {
    case 'LOGIN':
      return action.data
    case 'LOGOUT':
      return INITIAL_STATE
    case 'CHALLENGE':
      return action.data
    default:
      return INITIAL_STATE
  }
}

const store = createStore(authenticated)

export default store
