import React from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useFetch } from 'src/hooks/useFetch'

import Authentication from '../Authentication'
import { Container, Avatar, UserInfo, Auth } from './styles'

interface User {
	userId?: string;
	user: UserApi;
	score: number;
}

interface UserApi {
	userId: string;
	firstName: string;
	lastName: string;
	imageUrl: string;
}

const Navbar: React.FC = () => {
  const dispatch = useDispatch()
  const data = useSelector((state: any) => state.data)
  const dataAuth = data.auth
  const authenticated = dataAuth.authenticated
  const userId = dataAuth.user.id
  const dataChallenge = data.challenge
  const challengeName = dataChallenge.name

  const { data: globalScore } = useFetch<User[]>('http://localhost:4001/contests/global')
  const { data: listOfUsers } = useFetch<UserApi[]>('http://localhost:4001/contests/user')
  const { data: challengeScore } = useFetch<User[]>(`http://localhost:4001/contests/global/${challengeName}`)
  const { data: userScore } = useFetch<User[]>(`http://localhost:4001/contests/${userId}/${challengeName}`)

  if (globalScore && listOfUsers && challengeScore) {
    const newData = {
      data: {
        auth: dataAuth,
        challenge: dataChallenge,
        globalScore: globalScore,
        listOfUsers: listOfUsers,
        challengeScore: challengeScore
      }
    }

    dispatch({ type: 'CHALLENGE', data: newData })
  }
  console.log(useSelector((state: any) => state.data))
  return (
    <Container>
      {authenticated.authenticated ? (
        <UserInfo>
          <span>Welcome, {authenticated.user.name}!</span>
          <Avatar src={authenticated.user.image} />
        </UserInfo>
      ) : (
        <></>
      )}
      <Auth>
        <Authentication />
      </Auth>
    </Container>
  )
}

export default Navbar
