import { useDispatch, useSelector } from 'react-redux'

export function titleCase (str: string) {
  str = str.split('_').join(' ')
  const splitStr = str.toLowerCase().split(' ')
  for (let i = 0; i < splitStr.length; i++) {
    // You do not need to check if i is larger than splitStr length, as your for does that for you
    // Assign it back to the array
    splitStr[i] = splitStr[i].charAt(0).toUpperCase() + splitStr[i].substring(1)
  }
  // Directly return the joined string
  return splitStr.join(' ')
}

const dispatch = useDispatch()

const data = useSelector((state: any) => state.data)
const dataChallenge = data.challenge

export const login = (response: any) => {
  const newAuth = {
    data: {
      auth: {
        authenticated: true,
        user: {
          id: response.googleId,
          name: response.profileObj.givenName,
          surname: response.profileObj.familyName,
          image: response.profileObj.imageUrl
        },
        token: response.tokenId
      },
      challenge: dataChallenge
    }
  }
  dispatch({ type: 'LOGIN', data: newAuth })
}

export const badResponseGoogle = (response: any) => {
  console.log(response)
  dispatch({ type: 'BAD_RESPONSE', data: null })
}

export const logout = () => {
  const newAuth = {
    data: {
      auth: {
        authenticated: false,
        user: {
          id: null,
          name: null,
          surname: null,
          image: null
        },
        token: null
      },
      challenge: dataChallenge
    }
  }
  dispatch({ type: 'LOGOUT', data: newAuth })
}
