import React, { useState } from 'react'

import { Container, DropFiles, SubmitButton, DropFilesZone } from './styles'
import Dropzone from 'react-dropzone'
import apiCodeContest from '../../services/apiCodeContest'

import { Modal, Spinner, Col, Row } from 'react-bootstrap'

export interface Props {
  challengeName?: string;
}
interface BodyRequest {
  [key: string]: any
}
interface Push {
  data: {
    commit: {
      sha: string
    }
  }
  id: number
}

interface Run {
  data: {
    // eslint-disable-next-line camelcase
    workflow_runs: [{
      status: string,
      conclusion: string,
      // eslint-disable-next-line camelcase
      head_commit: {
        id: string
      },
      id: number
    }],
    // eslint-disable-next-line camelcase
    total_count: number
  }
}

const Drop: React.FC<Props> = ({ challengeName }) => {
  const [fileName, setFileName] = useState('')
  const [solution, setSolution] = useState('')
  const [inProgress, setInProgress] = useState(false)
  const [sendedFile, setSendedFile] = useState(false)
  const [currentChallengeName, setCurrentChallengeName] = useState<string>('challenge')
  const [bodyRequest, setBodyRequest] = useState <BodyRequest | null >(null)

  const [show, setShow] = useState(false)

  const { runsFetch } = useFetch<Challenge[]>('/actions/runs')

  const config = {
    headers: {
      Authorization: `token ${process.env.REACT_APP_TOKEN}`,
      Accept: 'application/vnd.github.antiope-preview+json'
    }
  }

  if (challengeName && challengeName !== currentChallengeName) {
    setCurrentChallengeName(challengeName)
    setShow(false)
    setFileName('')
    setBodyRequest(null)
    setSendedFile(false)
    setSolution('')
    setInProgress(false)
  }

  const handleDrop = (acceptedFile: any) => {
    setSendedFile(false)
    setFileName(acceptedFile.map((file: any) => {
      const reader = new FileReader()
      reader.readAsDataURL(file)
      reader.addEventListener('load', async () => {
        if (reader) {
          const result = (reader.result as string).split(',')[1]

          const bodyRequest: BodyRequest = {
            message: `${challengeName?.split(' ').join('_')}/user1`,
            committer: {
              name: 'minecodebot',
              email: 'minecode.geral@gmail.com'
            },
            content: `${result}`
          }

          try {
            const fileAlreadyExist = await apiCodeContest.get(`/contents/challenges/${challengeName?.split(' ').join('_')}/user1/resolution.py`, config)
            bodyRequest.sha = `${fileAlreadyExist.data.sha}`
          } catch (error) {
            console.log(error)
          }
          setBodyRequest(bodyRequest)
        }
      }, false)
      return file.name
    }))
  }

  const submitFile = async (bodyRequest: BodyRequest) => {
    setSolution('')
    setInProgress(true)
    const push: Push = await apiCodeContest.put(`/contents/challenges/${challengeName?.split(' ').join('_')}/user1/resolution.py`, bodyRequest, config)
    // eslint-disable-next-line no-var
    const commit = push.data.commit.sha
    let run: Run = await apiCodeContest.get('/actions/runs', config)
    while (run.data.workflow_runs[0].head_commit.id !== commit) {
      run = await apiCodeContest.get('/actions/runs', config)
    }
    let runState = run.data.workflow_runs[0].status
    const runId = run.data.workflow_runs[0].id
    while (runState !== 'completed') {
      run = await apiCodeContest.get(`/actions/runs/${runId}`, config)
      const logs = await apiCodeContest.get(`/actions/runs/${runId}/logs`, config)
      console.log(logs)
      runState = run.data.workflow_runs[0].status
    }
    setSolution(run.data.workflow_runs[0].conclusion)
    setInProgress(false)
    setShow(false)
    setSendedFile(true)
  }

  return (
    <Container>
      {challengeName
        ? <Dropzone
          onDrop={handleDrop}
        >
          {({
            getRootProps,
            getInputProps,
            isDragActive,
            isDragReject
          }) => {
            return (
              <DropFiles>
                <DropFilesZone {...getRootProps()}>
                  <input {...getInputProps()} />
                  <span>{isDragActive ? '📂' : '📁'}</span>
                  <p>
                    {fileName ? <>{fileName}</> : isDragReject ? <> Invalid file </> : <>Drag & drop images, or click to select files</>}
                  </p>
                </DropFilesZone>

                {inProgress ? <Modal show={show} centered>
                  <Modal.Body className="show-grid">
                    <Container>
                      <Row>
                        <Col xs={12} className="text-center">
                          <Spinner animation="border" />
                        </Col>
                        <Col xs={12} className="text-center">
                          <p>Testing your solution... Wait a moment, please!</p>
                        </Col>
                      </Row>
                    </Container>
                  </Modal.Body>
                </Modal> : bodyRequest && !sendedFile ? <SubmitButton onClick={() => { submitFile(bodyRequest); setShow(true) }}>Submit</SubmitButton> : sendedFile && solution ? <p>{solution}</p> : <></>}
              </DropFiles>
            )
          }}
        </Dropzone> : <></>
      }
    </Container>
  )
}

export default Drop
