import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

import Authentication from '../Authentication'
import { Container, Avatar, UserInfo, Auth } from './styles'

const Navbar: React.FC = () => {
  const dispatch = useDispatch()

  const contestChallenge = useSelector((state: any) => state.data)
  const challengeName = contestChallenge.challenge.name

  const { data: globalScore } = useFetch<User[]>('http://localhost:4001/contests/global')
  const { data: listOfUsers } = useFetch<UserApi[]>('http://localhost:4001/contests/user')
  const { data: challengeScore } = useFetch<User[]>(`http://localhost:4001/contests/global/${challengeName}`)

  const newAuth = {
    data: {
      auth: dataAuth,
      challenge: dataChallenge,
      globalScore: dataGlobalScore,
      listOfUsers: dataListOfUsers,
      challengeScore: dataChallengeScore
    }
  }

  dispatch({ type: 'CHALLENGE', data: newAuth })

  const authentication = useSelector((state: any) => state.data.auth)
  return (
    <Container>
      {authentication.authenticated ? (
        <UserInfo>
          <span>Welcome, {authentication.user.name}!</span>
          <Avatar src={authentication.user.image} />
        </UserInfo>
      ) : (
        <></>
      )}
      <Auth>
        <Authentication />
      </Auth>
    </Container>
  )
}

export default Navbar
